import React from 'react';
import PropTypes from 'prop-types';
import './Engagement.css';
import clappingImg from '../../assets/clapping.svg';
import blackHeartImg from '../../assets/heart-black.svg';
import redHeartImg from '../../assets/heart-red.svg';

function Engagement({ iniclapCount }) {
  const [liked, setLiked] = React.useState(false);
  const [clapped, setClapped] = React.useState(false);
  const [clapCount, setClapCount] = React.useState(clapCount);

  function handleClap() {
    if(clapped) {
      setClapCount(clapCount-1);
      setClapped(false);
    }else {
      setClapCount(clapCount+1);
      setClapped(true);
    }
  }

  function handleLike() {
    setLiked(!liked);
  }

  return (
    <div className="clap-heart">
      <span className="clap-and-count">
        <img src={clappingImg} alt="" onClick={handleClap}/>
        <span>{clapCount}</span>
      </span>

      <img src={liked ? redHeartImg : blackHeartImg} alt="" onClick={handleLike}/>
    </div>
  );
}

Engagement.propTypes = {
  clapCount: PropTypes.number.isRequired,
  liked: PropTypes.bool.isRequired,
};

export default Engagement;
